themeCSS:
.node rect, .node circle, .node polygon {
  rx: 6px;
  ry: 6px;
}
.flowchart-link path {
  stroke-width: 2px;
}
.edgeLabel text, .label text {
  fill: #0F172A;
  font-weight: 600;
}
.kv-accent rect, .kv-accent circle, .kv-accent polygon {
  fill: #3B82F6 !important;
  stroke: #1E40AF !important;
}
.kv-muted rect, .kv-muted circle, .kv-muted polygon {
  fill: #EFF6FF !important;
  stroke: #1E3A8A !important;
  stroke-dasharray: 6 4;
}
.kv-pattern rect, .kv-pattern circle, .kv-pattern polygon {
  fill: #DBEAFE !important;
  stroke: #1E3A8A !important;
  stroke-dasharray: 4 3;
}
.sectionTitle {
  fill: #0F172A !important;
}
