themeCSS:
:root {
  --kv-blue-950: #0B1220;
  --kv-blue-900: #0F172A;
  --kv-blue-800: #1E3A8A;
  --kv-blue-700: #1D4ED8;
  --kv-blue-600: #2563EB;
  --kv-blue-500: #3B82F6;
  --kv-blue-400: #60A5FA;
  --kv-blue-300: #93C5FD;
  --kv-blue-200: #BFDBFE;
  --kv-ice-100: #E0F2FE;
  --kv-ice-50: #F8FAFF;
}

body {
  font-family: "Inter", "Segoe UI", sans-serif;
  color: var(--kv-blue-900);
}

.node rect, .node circle, .node polygon {
  rx: 8px;
  ry: 8px;
  stroke-width: 2px;
}

.node text, .label text, .sectionTitle {
  fill: var(--kv-blue-900);
  font-weight: 600;
}

.edgeLabel text {
  fill: var(--kv-blue-900);
  font-weight: 600;
}

.edgeLabel rect {
  fill: var(--kv-ice-100);
  stroke: var(--kv-blue-700);
  stroke-width: 1.5px;
  rx: 6px;
  ry: 6px;
}

.edgePath path, .flowchart-link path {
  stroke: var(--kv-blue-700);
  stroke-width: 2px;
}

.marker path {
  fill: var(--kv-blue-700);
}

.cluster rect {
  fill: var(--kv-ice-100);
  stroke: var(--kv-blue-800);
  stroke-width: 2px;
  rx: 12px;
  ry: 12px;
}

.kv-primary rect, .kv-primary circle, .kv-primary polygon {
  fill: var(--kv-blue-800) !important;
  stroke: var(--kv-blue-950) !important;
  stroke-width: 2.5px !important;
}

.kv-primary text {
  fill: #F8FAFC !important;
}

.kv-accent rect, .kv-accent circle, .kv-accent polygon {
  fill: var(--kv-blue-600) !important;
  stroke: var(--kv-blue-900) !important;
  stroke-width: 2.5px !important;
}

.kv-accent text {
  fill: #F8FAFC !important;
}

.kv-highlight rect, .kv-highlight circle, .kv-highlight polygon {
  fill: var(--kv-blue-400) !important;
  stroke: var(--kv-blue-700) !important;
  stroke-width: 2.5px !important;
}

.kv-highlight text {
  fill: var(--kv-blue-900) !important;
}

.kv-muted rect, .kv-muted circle, .kv-muted polygon {
  fill: var(--kv-ice-100) !important;
  stroke: var(--kv-blue-700) !important;
  stroke-width: 2.5px !important;
  stroke-dasharray: 8 4 !important;
}

.kv-muted text {
  fill: var(--kv-blue-900) !important;
}

.kv-pattern rect, .kv-pattern circle, .kv-pattern polygon {
  fill: var(--kv-blue-300) !important;
  stroke: var(--kv-blue-900) !important;
  stroke-width: 2.5px !important;
  stroke-dasharray: 4 3 !important;
  stroke-linecap: round !important;
}

.kv-pattern text {
  fill: var(--kv-blue-950) !important;
}

.kv-outline rect, .kv-outline circle, .kv-outline polygon {
  fill: #FFFFFF !important;
  stroke: var(--kv-blue-800) !important;
  stroke-width: 3px !important;
}

.kv-outline text {
  fill: #0F172A !important;
  font-weight: 700 !important;
}

.kv-elevated rect, .kv-elevated circle, .kv-elevated polygon {
  fill: var(--kv-ice-50) !important;
  stroke: var(--kv-blue-700) !important;
  stroke-width: 2px !important;
  filter: drop-shadow(0 2px 4px rgba(15, 23, 42, 0.15));
}

.kv-elevated text {
  fill: var(--kv-blue-900) !important;
}

.mindmap-node circle, .mindmap-node rect {
  stroke: var(--kv-blue-800) !important;
  stroke-width: 2px !important;
}

.mindmap-node.leaf > rect, .mindmap-node.leaf > circle {
  fill: var(--kv-blue-400) !important;
}

.mindmap-node.leaf text {
  fill: #FFFFFF !important;
}

.mindmap-node.level-1 > rect, .mindmap-node.level-1 > circle {
  fill: var(--kv-blue-800) !important;
}

.mindmap-node.level-1 text {
  fill: #F8FAFC !important;
}

.mindmap-node.level-2 > rect, .mindmap-node.level-2 > circle {
  fill: var(--kv-blue-500) !important;
}

.mindmap-node.level-2 text {
  fill: #F8FAFC !important;
}

.sequenceMessageLine0, .sequenceMessageLine1, .sequenceMessageLine2 {
  stroke: var(--kv-blue-700) !important;
}

.sequenceNumber {
  fill: #FFFFFF !important;
  stroke: var(--kv-blue-800) !important;
  stroke-width: 2px !important;
}

.sequenceLabelBox {
  fill: var(--kv-ice-100) !important;
  stroke: var(--kv-blue-700) !important;
}

.actor {
  fill: var(--kv-blue-500) !important;
  stroke: var(--kv-blue-900) !important;
  color: #F8FAFC !important;
}

.actor text {
  fill: #F8FAFC !important;
}

.kv-legend rect {
  fill: #FFFFFF !important;
  stroke: var(--kv-blue-700) !important;
  stroke-width: 1.5px !important;
  rx: 8px;
  ry: 8px;
}

.kv-legend text {
  fill: var(--kv-blue-900) !important;
  font-size: 14px;
}

.quadrant rect {
  stroke-width: 0px !important;
}

.pieOuterCircle {
  stroke: var(--kv-blue-800) !important;
  stroke-width: 2px !important;
}
